CREATE OR REPLACE FUNCTION obter_med_pac_cid (nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w       varchar(255);
cd_cid_w           varchar(10);

c01 CURSOR FOR
   SELECT  cd_doenca_cid
   from    med_pac_cid
   where   nr_seq_cliente  = nr_seq_cliente_p;


BEGIN
open       c01;
loop
fetch      c01 into cd_cid_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
   begin
   ds_retorno_w    := ds_retorno_w || cd_cid_w || '-';
   end;
end loop;
close c01;

return     substr(ds_retorno_w,1,length(ds_retorno_w) -1);

end;
$body$
LANGUAGE PLPGSQL
