CREATE OR REPLACE FUNCTION obter_med_plano (NR_SEQ_PLANO_P bigint) RETURNS varchar AS $body$
DECLARE


DS_plano_w		varchar(40)	:= '';


BEGIN

SELECT	substr(DS_PLANO,1,40)
INTO STRICT	DS_plano_w
FROM	MED_PLANO
WHERE 	NR_SEQUENCIA	= NR_SEQ_PLANO_P;

return	DS_plano_w;

END;
