CREATE OR REPLACE FUNCTION obter_med_regra_patologia (nr_prescricao_p bigint, nr_seq_proc_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_medico_retorno_w	varchar(10);
cd_setor_atendimento_w	integer;
ie_tipo_regra_w		varchar(15);
			

BEGIN 
 
select	coalesce(cd_setor_atendimento,cd_setor_entrega) 
into STRICT	cd_setor_atendimento_w 
from	prescr_medica pm 
where	pm.nr_prescricao = nr_prescricao_p;
 
select	ie_tipo_regra 
into STRICT	ie_tipo_regra_w 
from	REGRA_LAUDO_PAT_MEDICO 
where	cd_setor_atendimento = cd_setor_atendimento_w;
 
if (ie_tipo_regra_w = 'N') then 
	 cd_medico_retorno_w := '';
elsif (ie_tipo_regra_w = 'RA') then 
	 select cd_medico_resp 
	 into STRICT	cd_medico_retorno_w 
	 from	atendimento_paciente 
	 where	nr_atendimento = nr_atendimento_p;
elsif (ie_tipo_regra_w = 'S') then 
	 select cd_medico_executor 
	 into STRICT	cd_medico_retorno_w 
	 from	procedimento_paciente 
	 where	nr_sequencia = nr_seq_proc_p;
elsif (ie_tipo_regra_w = 'SO') then 
	 select cd_medico 
	 into STRICT	cd_medico_retorno_w 
	 from	prescr_medica 
	 where	nr_prescricao = nr_prescricao_p;
end if;
 
return	cd_medico_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
