CREATE OR REPLACE FUNCTION obter_med_rotina_regra ( cd_setor_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w					bigint;
ie_medicamento_rotina_w		varchar(5);


BEGIN
if (coalesce(cd_setor_p,0) > 0) then

	ie_medicamento_rotina_w := 'N';

	select	count(*)
	into STRICT	qt_regra_w
	from	plt_parametros_setor
	where	cd_setor_atendimento = cd_setor_p;

	if (qt_regra_w > 0) then

		select	max(ie_medicamento_rotina)
		into STRICT	ie_medicamento_rotina_w
		from	plt_parametros_setor
		where	cd_setor_atendimento = cd_setor_p;

	end if;

end if;

return	ie_medicamento_rotina_w;

end;
$body$
