CREATE OR REPLACE FUNCTION obter_med_temp_espera_fila (nr_seq_fila_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ie_somente_nao_atendidas_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_tempo_media_espera_w	bigint;
ie_forma_tempo_espera_w	varchar(1);
dt_inicio_w		timestamp;
nr_minutos_w		bigint;
nr_horas_w		bigint;
ds_tempo_w		varchar(11);
qt_senhas_w		bigint;
nr_sequencia_w		bigint;
nr_seq_movimenacao_w	bigint;
qt_total_min_w		bigint;
qt_total_seg_w		bigint;
ds_tempo_espera_w	varchar(20);
qt_segundo_w 		bigint;
dt_entrada_w		timestamp;
dt_saida_w		timestamp;
dt_inicio_atendimento_w	timestamp;
ie_somente_nao_atendidas_w varchar(1);

C01 CURSOR FOR
	SELECT	a.nr_seq_pac_senha_fila,
		a.nr_sequencia,
		a.dt_entrada,
		a.dt_saida
	from	movimentacao_senha_fila a,
		paciente_senha_fila b
	where	a.nr_seq_fila_espera = nr_seq_fila_p
	and		trunc(a.dt_entrada) between trunc(dt_inicio_p) and (dt_fim_p)
	and	b.nr_sequencia = a.nr_seq_pac_senha_fila
	and	coalesce(b.dt_inutilizacao::text, '') = ''
	and	((b.nr_seq_fila_senha = a.nr_seq_fila_espera) or (exists (	SELECT	1
										from	atendimentos_senha c
										where	c.nr_seq_pac_senha_fila = a.nr_seq_pac_senha_fila
										and	c.nr_seq_fila_espera = a.nr_seq_fila_espera)));
										
										
C02 CURSOR FOR
	SELECT a.nr_seq_pac_senha_fila,
		a.nr_sequencia,
		a.dt_entrada,
		a.dt_saida
	FROM movimentacao_senha_fila a,
		 paciente_senha_fila b
	WHERE b.nr_sequencia = a.NR_SEQ_PAC_SENHA_FILA	
	AND coalesce(b.nr_seq_fila_senha, b.nr_seq_fila_senha_origem) = nr_seq_fila_p
	AND	TRUNC(a.dt_entrada) BETWEEN trunc(dt_inicio_p) AND (dt_fim_p)
	AND	coalesce(b.dt_inutilizacao::text, '') = ''
	AND (coalesce(b.nr_seq_fila_senha, b.nr_seq_fila_senha_origem) = a.nr_seq_fila_espera)
	AND ( NOT EXISTS (	SELECT	1
						FROM	atendimentos_senha c
						WHERE	c.NR_SEQ_PAC_SENHA_FILA = b.nr_sequencia
						AND c.NR_SEQ_FILA_ESPERA = a.nr_seq_fila_espera));



BEGIN

if (coalesce(ie_somente_nao_atendidas_p::text, '') = '') then
	ie_somente_nao_atendidas_w := 'N';
else
	ie_somente_nao_atendidas_w := ie_somente_nao_atendidas_p;
end if;

nr_minutos_w := 0;
nr_horas_w   := 0;
qt_senhas_w  := 0;
qt_total_min_w := 0;
qt_total_seg_w := 0;
qt_segundo_w := 0;

ie_forma_tempo_espera_w := Obter_param_Usuario(10021, 77, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_forma_tempo_espera_w);

if (ie_somente_nao_atendidas_p <> 'S') then
open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_movimenacao_w,
	dt_entrada_w,
	dt_saida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	If (ie_forma_tempo_espera_w = 'F') then
		select	max(dt_entrada)
		into STRICT	dt_inicio_w
		from	movimentacao_senha_fila
		where	nr_sequencia = nr_seq_movimenacao_w;

		If (coalesce(dt_inicio_w::text, '') = '') then
			select	max(coalesce(dt_entrada_fila, dt_geracao_senha))
			into STRICT	dt_inicio_w
			from	paciente_senha_fila a
			where	a.nr_sequencia = nr_sequencia_w;
		end if;
	else
		select	max(dt_geracao_senha)
		into STRICT	dt_inicio_w
		from	paciente_senha_fila
		where	nr_sequencia = nr_sequencia_w;
	end if;

	begin
		select	max(a.dt_inicio_atendimento)
		into STRICT	dt_inicio_atendimento_w
		from	atendimentos_senha a
		where	nr_seq_pac_senha_fila = nr_sequencia_w
		and	a.dt_inicio_atendimento between dt_entrada_w and coalesce(dt_saida_w, clock_timestamp())
		and	a.nr_seq_fila_espera = nr_seq_fila_p;
		
		if (coalesce(dt_inicio_atendimento_w::text, '') = '') then

			select	max(dt_primeira_chamada)
			into STRICT	dt_inicio_atendimento_w
			from	paciente_senha_fila
			where	nr_sequencia = nr_sequencia_w;

		end if;
		
	exception
		when	others then
			dt_inicio_atendimento_w := null;
	end;
	
	qt_senhas_w := qt_senhas_w + 1;
		begin
			qt_segundo_w := round((coalesce(dt_inicio_atendimento_w, clock_timestamp()) - dt_inicio_w) * 86400);
		exception
		when others then
			qt_segundo_w := 9999999999;
		end;

	while(qt_segundo_w > 60) loop
			nr_minutos_w := nr_minutos_w + 1;
			qt_segundo_w := qt_segundo_w - 60;
	end loop;
	qt_total_min_w := nr_minutos_w;
	end;
end loop;
close C01;
else
	open C02;
	loop
	fetch C02 into
		nr_sequencia_w,
		nr_seq_movimenacao_w,
		dt_entrada_w,
		dt_saida_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		If (ie_forma_tempo_espera_w = 'F') then
			select	max(dt_entrada)
			into STRICT	dt_inicio_w
			from	movimentacao_senha_fila
			where	nr_sequencia = nr_seq_movimenacao_w;

			If (coalesce(dt_inicio_w::text, '') = '') then
				select	max(coalesce(dt_entrada_fila, dt_geracao_senha))
				into STRICT	dt_inicio_w
				from	paciente_senha_fila a
				where	a.nr_sequencia = nr_sequencia_w;
			end if;
		else

			select	max(dt_geracao_senha)
			into STRICT	dt_inicio_w
			from	paciente_senha_fila
			where	nr_sequencia = nr_sequencia_w;

		end if;
	
		qt_senhas_w := qt_senhas_w + 1;
		begin
			qt_segundo_w := round((clock_timestamp() - dt_inicio_w) * 86400);
		exception
		when others then
			qt_segundo_w := 9999999999;
		end;

		while(qt_segundo_w > 60) loop
			nr_minutos_w := nr_minutos_w + 1;
			qt_segundo_w := qt_segundo_w - 60;
		end loop;
		qt_total_min_w := nr_minutos_w;
		end;
	end loop;
close C02;
end if;

if (coalesce(qt_senhas_w,0) > 0) then

	ds_tempo_media_espera_w := (qt_total_min_w/qt_senhas_w);
	
	while(ds_tempo_media_espera_w > 60) loop
		nr_horas_w := nr_horas_w + 1;
		ds_tempo_media_espera_w := ds_tempo_media_espera_w - 60;
	end loop;
	
	if (nr_horas_w < 100) then
		ds_tempo_w := lpad(nr_horas_w, 2, '0') || ':' || lpad(ds_tempo_media_espera_w, 2, '0') || ':' || lpad(coalesce(qt_segundo_w,'00'), 2, '0');
		
	elsif (nr_horas_w < 1000) then
		ds_tempo_w := lpad(nr_horas_w, 3, '0') || ':' || lpad(ds_tempo_media_espera_w, 2, '0') || ':' || lpad(coalesce(qt_segundo_w,'00'), 2, '0');
		
	elsif (nr_horas_w < 10000) then
		ds_tempo_w := lpad(nr_horas_w, 4, '0') || ':' || lpad(ds_tempo_media_espera_w, 2, '0') || ':' || lpad(coalesce(qt_segundo_w,'00'), 2, '0');
		
	else
		ds_tempo_w := lpad(nr_horas_w, 5, '0') || ':' || lpad(ds_tempo_media_espera_w, 2, '0') || ':' || lpad(coalesce(qt_segundo_w,'00'), 2, '0');
	end if;
	

	ds_tempo_espera_w := ds_tempo_w;

end if;

If (coalesce(ds_tempo_espera_w::text, '') = '') then
	ds_tempo_espera_w := '00:00:00';
end if;

return	ds_tempo_espera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_med_temp_espera_fila (nr_seq_fila_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ie_somente_nao_atendidas_p text default 'N') FROM PUBLIC;

