CREATE OR REPLACE FUNCTION obter_med_turno_trabalho (dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_turno_w		varchar(20);


BEGIN

if (dt_referencia_p > to_date(to_char(dt_referencia_p,'dd/mm/yyyy') || ' 07:29:59','dd/mm/yyyy hh24:mi:ss')) and (dt_referencia_p < to_date(to_char(dt_referencia_p,'dd/mm/yyyy') || ' 13:29:59','dd/mm/yyyy hh24:mi:ss')) then
	ds_turno_w	:= wheb_mensagem_pck.get_texto(308636); -- Matutino
elsif (dt_referencia_p > to_date(to_char(dt_referencia_p,'dd/mm/yyyy') || ' 13:29:59','dd/mm/yyyy hh24:mi:ss')) and (dt_referencia_p < to_date(to_char(dt_referencia_p,'dd/mm/yyyy') || ' 19:29:59','dd/mm/yyyy hh24:mi:ss')) then
	ds_turno_w	:= wheb_mensagem_pck.get_texto(308637); -- Vespertino
else
	ds_turno_w	:= wheb_mensagem_pck.get_texto(308638); -- Noturno
end if;

return	ds_turno_w;

end;
$body$
