SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_menor_codigo_expressao () RETURNS bigint AS $body$
DECLARE


c01 CURSOR FOR
SELECT	a.cd_expressao
from	dic_expressao a
order by a.cd_expressao;

nr_retorno_w	bigint;
old_w		bigint;

BEGIN

for reg in c01 loop
	begin
	if	(reg.cd_expressao > (old_w + 1)) then
		nr_retorno_w	:= (old_w + 1);
		exit;
	end if;
	old_w	:= reg.cd_expressao;
	end;
end loop;

return nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
