CREATE OR REPLACE FUNCTION obter_menor_codigo_funcao ( ds_aplicacao_p text default null) RETURNS bigint AS $body$
DECLARE


cd_funcao_w		integer;
cd_funcao_old_w	integer;
cd_retorno_w		integer;
ds_aplicao_w   varchar(10);

C01 CURSOR FOR
	SELECT	cd_funcao
	from	funcao
	where	cd_funcao > 0
	order by 1;


BEGIN

ds_aplicao_w := ds_aplicacao_p;

open C01;
loop
	fetch C01 into
		cd_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if	(cd_funcao_w > (cd_funcao_old_w + 1)) then
			cd_retorno_w	:= (cd_funcao_old_w + 1);

      if (( ds_aplicacao_p <> 'TasyApps' ) and (( cd_retorno_w between 40000 and 49999 ) or ( cd_retorno_w between 70000 and 79999 )))  then
       begin
        cd_funcao_old_w	:= cd_funcao_w;
       end;
      else if (( ds_aplicacao_p = 'TasyApps' ) and ( not (( cd_retorno_w between 40000 and 49999 ) or ( cd_retorno_w between 70000 and 79999 ))))  then
       begin
        cd_funcao_old_w	:= cd_funcao_w;
       end;
      else
        exit;
      end if; 			
     end if;
		end	if;
		cd_funcao_old_w	:= cd_funcao_w;
	end;
end loop;
close C01;

Return	cd_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
