CREATE OR REPLACE FUNCTION obter_menor_data_entrega_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_entrega_w	timestamp;


BEGIN

select	coalesce(min(dt_prevista_entrega), clock_timestamp())
into STRICT	dt_entrega_w
from	ordem_compra_item_entrega
where	nr_ordem_compra	= nr_ordem_compra_p
and	nr_item_oci	= nr_item_oci_p;

return dt_entrega_w;

end;
$body$
