CREATE OR REPLACE FUNCTION obter_menor_entrega_vigente_sc (nr_solic_compra_p bigint) RETURNS timestamp AS $body$
DECLARE



dt_entrega_solicitada_w	timestamp;


BEGIN

select	min(x.dt_entrega_solicitada)
into STRICT	dt_entrega_solicitada_w
from	solic_compra_item_entrega x
where	x.nr_solic_compra = nr_solic_compra_p
and	trunc(x.dt_entrega_solicitada,'dd') >= trunc(clock_timestamp(),'dd');

return	dt_entrega_solicitada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
