CREATE OR REPLACE FUNCTION obter_menor_estagio_licitacao () RETURNS bigint AS $body$
DECLARE


/*retorna o estagio da licitação com o menor sequencia cronologica*/

nr_sequencia_w			bigint;


BEGIN

select	min(nr_sequencia)
into STRICT	nr_sequencia_w
from	lic_estagio
where	nr_seq_cron = (
	SELECT	min(nr_seq_cron)
	from	lic_estagio);

return	nr_sequencia_w;

end;
$body$
