SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_menor_horario (ds_horarios_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10) := '';
cont_espaco_w 	integer;
ds_horarios_w	varchar(2000);
cont_ponto_w	integer;
ds_hor_w		varchar(10);
ds_menor_hor_w	varchar(10);
i		bigint := 0;


BEGIN

ds_horarios_w	:= trim(both ds_horarios_p);
ds_horarios_w	:= replace(ds_horarios_w, '  ', ' ');
ds_menor_hor_w := null;

while(length(ds_horarios_w) > 1) and (i<50) loop
begin
cont_espaco_w 	:= position(' ' in ds_horarios_w);

if (cont_espaco_w > 0) then
	ds_hor_w := substr(ds_horarios_w, 1, cont_espaco_w);
	ds_horarios_w := substr(ds_horarios_w, cont_espaco_w +1, length(ds_horarios_w));
else
	ds_hor_w := ds_horarios_w;
	ds_horarios_w := null;
end if;

if (coalesce(ds_menor_hor_w::text, '') = '') then
	ds_menor_hor_w	:= ds_hor_w;
else
	if (somente_numero(ds_menor_hor_w) > somente_numero(ds_hor_w)) then
		ds_menor_hor_w := ds_hor_w;
	end if;
end if;

i := i+1;
end;
end loop;

ds_horarios_w	:= ds_menor_hor_w;


if (obter_se_somente_numero(ds_horarios_p) = 'S') then
	cont_espaco_w 	:= position(' ' in ds_horarios_w);
	cont_ponto_w	:= position(':' in ds_horarios_w);

	if (cont_espaco_w = 2)	then
		ds_retorno_w	:=	'0'	||	substr(ds_horarios_w,1,1)	||':00';
	elsif (cont_espaco_w = 3) and (substr(ds_horarios_w,1,2) < 24) then
		ds_retorno_w 	:=	substr(ds_horarios_w,1,2)	||':00';
	elsif (cont_espaco_w = 0) then
		if (cont_ponto_w = 3) then
			if (length(ds_horarios_w) = 5)	then
				ds_retorno_w	:=	ds_horarios_w;
			elsif (length(ds_horarios_w) = 3) then
				ds_retorno_w	:=	ds_horarios_w	||'00';
			elsif (length(ds_horarios_w) = 4) then
				ds_retorno_w 	:=	ds_horarios_w	||'0';
			end if;
		elsif	((length(ds_horarios_w) = 2) and (substr(ds_horarios_w,1,2) < 24))	then
			ds_retorno_w	:=	substr(ds_horarios_w,1,2)	||':00';
		elsif	((length(ds_horarios_w) = 1) and (substr(ds_horarios_w,1,1) <> ':'))  then
			ds_retorno_w	:=	'0'	||	substr(ds_horarios_w,1,1)	||':00';
		end if;
	end if;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
