CREATE OR REPLACE FUNCTION obter_menor_idade (cd_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


qt_idade_w 	varchar(15);
dt_nascimento_w timestamp;


BEGIN

select 	SUBSTR(obter_idade_pf(cd_paciente_p,clock_timestamp(),'A'),1, 255)
into STRICT 	qt_idade_w
;

if (qt_idade_w <> 0) then
	return qt_idade_w;
end if;

select 	SUBSTR(obter_idade_pf(cd_paciente_p,clock_timestamp(),'M'),1, 255)
into STRICT 	qt_idade_w
;

if (qt_idade_w <> 0) then
	return qt_idade_w;
end if;

select	SUBSTR(obter_idade_pf(cd_paciente_p,clock_timestamp(),'DIA'),1, 255)
into STRICT 	qt_idade_w
;

if (qt_idade_w > 1) then
	return qt_idade_w;
end if;

select	max(dt_nascimento)
into STRICT	dt_nascimento_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_paciente_p;

qt_idade_w := (clock_timestamp() - dt_nascimento_w) * 24;

return qt_idade_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
