CREATE OR REPLACE FUNCTION obter_mensagem_adm_adep_js ( nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w		varchar(255);
cd_setor_regra_w	varchar(255);
cd_unidade_medida_w	varchar(255);

c01 CURSOR FOR
SELECT	ds_mensagem
from	REGRA_MENSAGEM_ADM_ITEM
where	((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_regra_w = cd_setor_atendimento))
and	cd_unidade_medida	= cd_unidade_medida_w
order by coalesce(cd_setor_atendimento,0),
	cd_unidade_medida;


BEGIN

select	max(x.cd_setor_atendimento)
into STRICT	cd_setor_regra_w
from	prescr_mat_hor a,
	prescr_medica x
where	a.nr_prescricao	= x.nr_prescricao
and	a.nr_sequencia	= nr_seq_horario_p;

select	max(x.cd_unidade_medida_dose)
into STRICT	cd_unidade_medida_w
from	prescr_mat_hor a,
	prescr_material x
where	a.nr_prescricao	= x.nr_prescricao
and	a.nr_seq_material = x.nr_sequencia
and	a.nr_sequencia = nr_seq_horario_p;

open C01;
loop
fetch C01 into
	ds_mensagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_mensagem_w	:= ds_mensagem_w;
end loop;
close C01;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
