CREATE OR REPLACE FUNCTION obter_mensagem_beira_leito (ie_tipo_mensagem_p text) RETURNS varchar AS $body$
DECLARE


retorno_w	varchar(255);
qt_mensagem_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_mensagem_w
from	regra_mensagem_check
where	ie_tipo_mensagem = upper(ie_tipo_mensagem_p);

if (qt_mensagem_w > 0) then
	begin

	select	max(ds_mensagem)
	into STRICT	retorno_w
	from	regra_mensagem_check
	where	ie_tipo_mensagem = upper(ie_tipo_mensagem_p);

	end;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
