SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mensagem_checkup ( nr_seq_mensagem text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(2000);
nm_pessoa_fisica_w	varchar(60);
ds_senha_w		varchar(20);


BEGIN 
 
select	ds_mensagem_padrao 
into STRICT	ds_retorno_w 
from	checkup_comunicacao 
where	nr_sequencia		= nr_seq_mensagem;
 
select	SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,60), 
	ds_senha 
into STRICT	nm_pessoa_fisica_w, 
	ds_senha_w 
from	pessoa_fisica 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
 
ds_retorno_w	:= replace(ds_retorno_w,'@cliente',nm_pessoa_fisica_w);
ds_retorno_w	:= replace(ds_retorno_w,'@usuario',cd_pessoa_fisica_p);
ds_retorno_w	:= replace(ds_retorno_w,'@senha',ds_senha_w);
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
