CREATE OR REPLACE FUNCTION obter_mensagem_especialidade ( cd_especialidade_p text, ie_finalidade_p text default 'B') RETURNS varchar AS $body$
DECLARE


ds_mensagem_w	especialidade_mensagem.ds_mensagem%type;
ds_retorno_w	varchar(2000);


BEGIN

select	max(ds_mensagem)
into STRICT	ds_mensagem_w
from	especialidade_mensagem
where	cd_especialidade = cd_especialidade_p
and		ie_finalidade = ie_finalidade_p
and		ie_situacao = 'A';

if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then
	ds_retorno_w	:= 	replace(ds_mensagem_w, '@DS_ESPECIALIDADE', obter_ds_especialidade(cd_especialidade_p));
end if;	

return	ds_retorno_w;

end;
$body$
