CREATE OR REPLACE FUNCTION obter_mensagem_leito_eup ( cd_setor_atendimento_p bigint, nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


nr_Sequencia_w			bigint;
ds_mensagem_w			varchar(255);


BEGIN

select	coalesce(max(nr_Sequencia),0)
into STRICT	nr_sequencia_w
from	regra_mensagem_leito_eup
where	cd_setor_atendimento = cd_setor_atendimento_p
and	((obter_desc_leito_unid(nr_seq_interno) = obter_desc_leito_atend(nr_seq_interno_p)) or (coalesce(nr_seq_interno::text, '') = ''))
and	ie_situacao = 'A';

if (nr_sequencia_w > 0) then
	select	ds_mensagem
	into STRICT	ds_mensagem_w
	from 	regra_mensagem_leito_eup
	where	nr_sequencia = nr_sequencia_w;
end if;

RETURN ds_mensagem_w;
end;
$body$
LANGUAGE PLPGSQL
