SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_meses_entre_datas (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w	timestamp;
qt_meses_w	bigint	:= 0;


BEGIN
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	dt_inicial_w	:= to_char(dt_inicial_p, 'mm/yyyy');
	dt_final_w	:= to_char(dt_final_p, 'mm/yyyy');

	qt_meses_w	:= round(dt_final_w - dt_inicial_w);
end if;

return	qt_meses_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
