CREATE OR REPLACE FUNCTION obter_mes_ano_dt (dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);

/* Opção
     M - Mês
     A - Ano
*/
BEGIN

if (ie_opcao_p = 'M') then
	select	PKG_DATE_UTILS.extract_field('MONTH', dt_referencia_p)
	into STRICT	ds_retorno_w
	;
elsif (ie_opcao_p = 'A') then
	select	PKG_DATE_UTILS.extract_field('YEAR',dt_referencia_p)
	into STRICT	ds_retorno_w
	;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
