CREATE OR REPLACE FUNCTION obter_mes_extenso ( nr_mes_p bigint, ie_tipo_informacao_p text) RETURNS varchar AS $body$
DECLARE


ds_mes_w	varchar(200);
ds_mes_abrev_w	varchar(200);

/*
Tipos de Informação:
'C' - Completo;
'A' - Abreviado;
'I' - Completo em Inglês;		* Paramêtros inutilizados para a Tradução do Sistema
'AI' - Abreviado em Inglês; 	* Paramêtros inutilizados para a Tradução do Sistema
*/
BEGIN
CASE nr_mes_p
WHEN 1 THEN
	BEGIN
	ds_mes_w	:= wheb_mensagem_pck.get_texto(308656); -- Janeiro
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308750); -- Jan
	END;
WHEN 2 THEN
	BEGIN
	ds_mes_w	:= wheb_mensagem_pck.get_texto(308751); -- Fevereiro
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308752); -- Fev
	END;
WHEN 3 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308753); -- Março
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308754); -- Mar
	END;
WHEN 4 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308755); -- Abril
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308756); -- Abr
	END;
WHEN 5 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308758); -- Maio
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308758); -- Maio
	END;
WHEN 6 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308759); -- Junho
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308784); -- Jun
	END;
WHEN 7 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308760); -- Julho
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308761); -- Jul
	END;
WHEN 8 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308762); -- Agosto
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308763); -- Ago
	END;
WHEN 9 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308764); -- Setembro
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308765); -- Set
	END;
WHEN 10 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308766); -- Outubro
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308767); -- Out
	END;
WHEN 11 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308768); -- Novembro
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308769); -- Nov
	END;
WHEN 12 THEN
	BEGIN
	ds_mes_w 	:= wheb_mensagem_pck.get_texto(308770); -- Dezembro
	ds_mes_abrev_w	:= wheb_mensagem_pck.get_texto(308771); -- Dez
	END;
ELSE
	BEGIN
	ds_mes_w	:= '';
	ds_mes_abrev_w	:= '';
	END;
END CASE;

IF (ie_tipo_informacao_p IN ('A','AI')) THEN
	BEGIN
	ds_mes_w := ds_mes_abrev_w;
	END;
END IF;

RETURN ds_mes_w;
END;
$body$
