CREATE OR REPLACE FUNCTION obter_mes_rel_orc (nr_seq_mes_inicio_p bigint, nr_seq_mes_fim_p bigint, nr_mes_ref_p bigint, ie_formato_p bigint) RETURNS varchar AS $body$
DECLARE


/*  Função criada especificamente para o hospital Rim*/

/*ie_formato_p

1 = MON/YYYY
2 = DD/MM/YYYY

*/
/* nr_mes_ref_p
0 à 11
*/
ds_retorno_w	varchar(20);


BEGIN

ds_retorno_w := null;

if (to_date(PKG_DATE_UTILS.start_of(PKG_DATE_UTILS.ADD_MONTH(ctb_obter_mes_ref(nr_seq_mes_inicio_p),nr_mes_ref_p, 0),'dd', 0),'DD/MM/YYYY') <= to_date(pkg_date_utils.start_of(ctb_obter_mes_ref(nr_seq_mes_fim_p),'DD',0),'dd/mm/yyyy')) then
	if (ie_formato_p = 1) then
		ds_retorno_w := to_char(PKG_DATE_UTILS.ADD_MONTH(ctb_obter_mes_ref(nr_seq_mes_inicio_p),nr_mes_ref_p, 0),'MON/YYYY');
	elsif (ie_formato_p = 2) then
		ds_retorno_w := to_char(PKG_DATE_UTILS.ADD_MONTH(ctb_obter_mes_ref(nr_seq_mes_inicio_p),nr_mes_ref_p, 0),'DD/MM/YYYY');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
