CREATE OR REPLACE FUNCTION obter_mes_trab_ano (dt_admissao_p timestamp, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


/* Function específica somente para um relatório - CMAN3836 */

qt_dias_w		double precision;


BEGIN

if (dt_admissao_p <= dt_inicial_p) then
	qt_dias_w	:= dt_final_p - dt_inicial_p;
else
	qt_dias_w	:= dt_final_p - dt_admissao_p;
end if;

return	qt_dias_w;

end;
