CREATE OR REPLACE FUNCTION obter_meta_bsc_sla (nr_seq_indicador_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_meta_w			double precision;


BEGIN

select	coalesce(max(a.qt_meta),95)
into STRICT		qt_meta_w
from		bsc_ind_inf a
where 	a.nr_seq_indicador = nr_seq_indicador_p
and 		to_date(bsc_obter_periodo_inf(a.nr_seq_indicador, a.nr_sequencia, a.cd_ano, a.cd_periodo),'mm/yyyy') = trunc(dt_referencia_p,'month');

return qt_meta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
