CREATE OR REPLACE FUNCTION obter_meta_kpi_bsc ( nr_seq_indicador_p bigint, ref_date_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_meta_w bsc_ind_inf.qt_meta%type;


BEGIN
	select max(qt_meta)
	into STRICT qt_meta_w
	from bsc_ind_inf_v
	where nr_seq_indicador = nr_seq_indicador_p
	and bsc_obter_data_inf(nr_seq_inf) = Trunc(ref_date_p, 'Month');
	
	return qt_meta_w;

end;
