CREATE OR REPLACE FUNCTION obter_meta_semana_desen (dt_referencia_p timestamp, nr_seq_gerencia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_meta_w	timestamp;
dt_retorno_w	timestamp;

C01 CURSOR FOR
	SELECT	a.dt_meta
	from	desenvolvimento_meta_os a, grupo_desenvolvimento b
	where	coalesce(a.qt_meta_real::text, '') = ''
	and	a.nr_seq_grupo_des = b.nr_sequencia
	and	b.nr_seq_gerencia  = nr_seq_gerencia_p
	and	trunc(a.dt_meta) between trunc(dt_referencia_p) and trunc(dt_referencia_p) + 10
	order by a.dt_meta;


BEGIN

open C01;
loop
fetch C01 into
	dt_meta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	dt_retorno_w	:= dt_meta_w;

	end;
end loop;
close C01;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
