CREATE OR REPLACE FUNCTION obter_metodo_prescricao (nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_metodo_w		bigint;


BEGIN

select 	max(nr_seq_metodo)
into STRICT	nr_seq_metodo_w
from	exame_lab_result_item a,
	exame_lab_resultado b
where	nr_prescricao = nr_prescricao_p
and	a.nr_seq_resultado = b.nr_seq_resultado
and	nr_seq_prescr = nr_seq_prescr_p;

if (ie_opcao_p = 1) then
	ds_retorno_w	:= nr_seq_metodo_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w	:= Obter_Metodo_Exame_Lab(nr_seq_metodo_w,2);
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
