CREATE OR REPLACE FUNCTION obter_microorganismo_precaucao (nr_seq_precaucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_micro_w		varchar(255);
cd_microorganismo_w	numeric(20);

C01 CURSOR FOR
	SELECT	cd_microorganismo
	from	atend_precaucao_micro
	where	nr_seq_atend_precaucao = nr_seq_precaucao_p;


BEGIN


open C01;
loop
fetch C01 into
	cd_microorganismo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select 	max(ds_microorganismo)
	into STRICT	ds_micro_w
	from 	cih_microorganismo
	where	cd_microorganismo = cd_microorganismo_w;

	ds_retorno_w := 	ds_retorno_w||' / '||ds_micro_w;

	end;
end loop;
close C01;
ds_retorno_w :=	substr(ds_retorno_w,4,length(ds_retorno_w));
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
