CREATE OR REPLACE FUNCTION obter_microregiao_paciente (NR_CEP_CIDADE_NASC_p text, cd_municipio_ibge_p text) RETURNS varchar AS $body$
DECLARE


ds_microregiao_w	varchar(100);


BEGIN

/*
Rafael 21/1/8 tratar munic√≠pio IBGE x Cidade Natal
*/
if (nr_cep_cidade_nasc_p IS NOT NULL AND nr_cep_cidade_nasc_p::text <> '') then

	select	max(b.ds_microregiao)
	into STRICT	ds_microregiao_w
	from	microregiao b,
		microregiao_municipio a
	where	a.nr_seq_microregiao	= b.nr_sequencia
	and	a.cd_cep_municipio	= somente_numero(NR_CEP_CIDADE_NASC_p);

elsif (cd_municipio_ibge_p IS NOT NULL AND cd_municipio_ibge_p::text <> '') then

	select	max(b.ds_microregiao)
	into STRICT	ds_microregiao_w
	from	microregiao b,
		microregiao_municipio a
	where	a.nr_seq_microregiao	= b.nr_sequencia
	and	a.cd_municipio_ibge	= somente_numero(cd_municipio_ibge_p);

end if;

return ds_microregiao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
