SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_microregiao_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ds_microregiao_w	varchar(100);
nr_cep_cidade_nasc_w	varchar(100);
cd_municipio_ibge_w	varchar(100);


BEGIN 
 
select 	obter_compl_pf(cd_pessoa_fisica_p,1,'CEP'), 
	obter_compl_pf(cd_pessoa_fisica_p,1,'CDM') 
into STRICT	nr_cep_cidade_nasc_w, 
	cd_municipio_ibge_w
;
 
 
if (nr_cep_cidade_nasc_w IS NOT NULL AND nr_cep_cidade_nasc_w::text <> '') then 
 
	select	max(b.ds_microregiao) 
	into STRICT	ds_microregiao_w 
	from	microregiao b, 
		microregiao_municipio a 
	where	a.nr_seq_microregiao	= b.nr_sequencia 
	and	a.cd_cep_municipio	= somente_numero(NR_CEP_CIDADE_NASC_w);
 
end if;
 
if (cd_municipio_ibge_w IS NOT NULL AND cd_municipio_ibge_w::text <> '') then 
 
	select	max(b.ds_microregiao) 
	into STRICT	ds_microregiao_w 
	from	microregiao b, 
		microregiao_municipio a 
	where	a.nr_seq_microregiao	= b.nr_sequencia 
	and	a.cd_municipio_ibge	= somente_numero(cd_municipio_ibge_w);
	 
end if;
 
 
return ds_microregiao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_microregiao_pf (cd_pessoa_fisica_p text) FROM PUBLIC;

