SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_minutos_atraso_agenda (nr_seq_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


nr_minutos_atraso_w	bigint;
dt_chegada_paciente_w	timestamp;
dt_agenda_w		timestamp;
dt_entrada_w		timestamp;


BEGIN

nr_minutos_atraso_w	:= null;
dt_agenda_w		:= null;

select	max(dt_agenda)
into STRICT	dt_agenda_w
from	agenda_consulta
where	nr_sequencia		= nr_seq_agenda_p
and	ie_status_agenda	not in ('C', 'B', 'F', 'I')
and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');

if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then
	select	max(dt_entrada)
	into STRICT	dt_entrada_w
	from	med_atendimento
	where	nr_seq_agenda	= nr_seq_agenda_p;
	if (coalesce(dt_entrada_w::text, '') = '') then
		dt_entrada_w 	:= clock_timestamp();
	end if;
end if;


if (dt_agenda_w < dt_entrada_w) and
	(dt_agenda_w > (clock_timestamp() - interval '120 days')) then
	nr_minutos_atraso_w := trunc((dt_entrada_w - dt_agenda_w) * 1440);
end if;

return	nr_minutos_atraso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_minutos_atraso_agenda (nr_seq_agenda_p bigint) FROM PUBLIC;

