CREATE OR REPLACE FUNCTION obter_minutos_espera_agenda (nr_seq_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


ie_status_agenda_w	varchar(2);
dt_entrada_w		timestamp;
dt_inicio_consulta_w	timestamp;
retorno_w		bigint;


BEGIN

select	max(dt_entrada),
	coalesce(max(dt_inicio_consulta), clock_timestamp())
into STRICT	dt_entrada_w,
	dt_inicio_consulta_w
from	med_atendimento
where	nr_seq_agenda	= nr_seq_agenda_p;

select	ie_status_agenda
into STRICT	ie_status_agenda_w
from	agenda_consulta
where	nr_sequencia		= nr_seq_agenda_p
and	ie_status_agenda	not in ('C', 'B', 'F', 'I')
and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');

if (dt_entrada_w IS NOT NULL AND dt_entrada_w::text <> '') then
	begin
	select 	to_number(CASE WHEN coalesce(dt_entrada_w::text, '') = '' THEN  null  ELSE CASE WHEN ie_status_agenda_w='N' THEN  Null  ELSE round((coalesce(dt_inicio_consulta_w, clock_timestamp()) - dt_entrada_w) *			1440) END  END )
	into STRICT	retorno_w
	;
	exception
		when others then
		retorno_w	:= 0;
	end;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
