CREATE OR REPLACE FUNCTION obter_minutos_espera_param ( dt_inicial_p timestamp, dt_final_p timestamp, dt_final_param_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_min_espera_w	bigint := 0;
dt_final_w	timestamp;


BEGIN

if (dt_final_p > dt_final_param_p) then
	dt_final_w := dt_final_param_p;
else
	dt_final_w := dt_final_p;
end if;

begin
select	round((coalesce(dt_final_w, clock_timestamp()) -  dt_inicial_p) * 1440)
into STRICT	qt_min_espera_w
;
exception
when 	others then
	qt_min_espera_w	:= 0;
end;

return	qt_min_espera_w;

end;
