CREATE OR REPLACE FUNCTION obter_minutos_hora ( ds_hora_p text) RETURNS bigint AS $body$
DECLARE


qt_minutos_w	integer := null;


BEGIN

if (ds_hora_p IS NOT NULL AND ds_hora_p::text <> '') then
	qt_minutos_w := (campo_numerico(substr(lpad(ds_hora_p,5,'0'),1,2)) * 60)  + (campo_numerico(substr(lpad(ds_hora_p,5,'0'),4,5)));
end if;

return	qt_minutos_w;

end;
