CREATE OR REPLACE FUNCTION obter_min_disp_equip ( nr_seq_equip_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_min_disp_w			double precision;
qt_min_parado_w			double precision;
qt_min_carga_w			double precision;
qt_retorno_w			double precision;


BEGIN

qt_min_disp_w	:= 0;
qt_retorno_w	:= 0;

select	coalesce(max(qt_carga_horaria),24) * 60
into STRICT	qt_min_carga_w
from	man_equipamento
where	nr_sequencia = nr_seq_equip_p;

if (trunc(clock_timestamp(),'month') = trunc(dt_referencia_p,'month')) then
	select (campo_numerico(to_char(clock_timestamp(),'dd')) * qt_min_carga_w)
	into STRICT	qt_min_disp_w
	;
else
	select (campo_numerico(to_char(last_day(dt_referencia_p),'dd')) * qt_min_carga_w)
	into STRICT	qt_min_disp_w
	;
end if;

select	coalesce(sum((dt_fim_real - dt_ordem_servico) * qt_min_carga_w),0)
into STRICT	qt_min_parado_w
from	man_ordem_servico
where	nr_seq_equipamento = nr_seq_equip_p
and	ie_parado = 'S'
and	(dt_inicio_real IS NOT NULL AND dt_inicio_real::text <> '')
and	(dt_fim_real IS NOT NULL AND dt_fim_real::text <> '')
and	dt_fim_real > dt_inicio_real
and	dt_ordem_servico between trunc(dt_referencia_p,'month') and last_day(trunc(dt_referencia_p,'month')) + 86399/86400;

if (ie_opcao_p = 'T') then
	qt_retorno_w	:= qt_min_disp_w;
elsif (ie_opcao_p = 'P') then
	qt_retorno_w	:= qt_min_parado_w;
elsif (ie_opcao_p = 'D') then
	qt_retorno_w	:= (qt_min_disp_w - qt_min_parado_w);
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
