CREATE OR REPLACE FUNCTION obter_min_disp_tipo_equip ( nr_seq_tipo_equip_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_Min_disp_w			bigint;
qt_Min_Parado_w			bigint;
qt_retorno_w			bigint;
qt_equipamento_w			bigint	:= 0;


BEGIN
qt_Min_disp_w	:= 0;
qt_retorno_w	:= 0;

if (trunc(clock_timestamp(),'month') = trunc(dt_referencia_p,'month')) then
	select (Campo_Numerico(to_char(clock_timestamp(),'dd')) * 1440)
	into STRICT	qt_min_disp_w
	;
else
	select (Campo_Numerico(to_char(last_day(dt_referencia_p),'dd')) * 1440)
	into STRICT	qt_min_disp_w
	;
end if;

select	count(*)
into STRICT	qt_equipamento_w
from	man_equipamento
where	nr_seq_tipo_equip = nr_seq_tipo_equip_p
and	ie_situacao = 'A';

qt_min_disp_w	:= (qt_min_disp_w * qt_equipamento_w);

select	coalesce(sum((a.dt_fim_real - a.dt_ordem_servico) * 1440),0)
into STRICT	qt_min_parado_w
from	man_equipamento e,
	man_ordem_servico a
where	a.nr_seq_equipamento	= e.nr_sequencia
and	e.nr_seq_tipo_equip	= nr_seq_tipo_equip_p
and	a.ie_parado		= 'S'
and	(a.dt_inicio_real IS NOT NULL AND a.dt_inicio_real::text <> '')
and	(a.dt_fim_real IS NOT NULL AND a.dt_fim_real::text <> '')
and	a.dt_fim_real > a.dt_inicio_real
and	a.dt_ordem_servico between trunc(dt_referencia_p,'month') and last_day(trunc(dt_referencia_p,'month')) + 86399/86400;

if (ie_opcao_p = 'T') then
	qt_retorno_w	:= qt_min_disp_w;
elsif (ie_opcao_p = 'P') then
	qt_retorno_w	:= qt_min_parado_w;
elsif (ie_opcao_p = 'D') then
	qt_retorno_w	:= (qt_min_disp_w - qt_min_parado_w);
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
