CREATE OR REPLACE FUNCTION obter_min_entre_datas_fer ( cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, qt_min_ref_p bigint) RETURNS bigint AS $body$
DECLARE


dt_atual_w		timestamp;
dt_anterior_w		timestamp;
qt_minuto_w		bigint;
qt_min_ref_w		smallint;
ie_dia_semana_w		varchar(01);
ie_feriado_w		bigint;


BEGIN

if (coalesce(dt_inicial_p::text, '') = '') or (coalesce(dt_final_p::text, '') = '') then
	qt_minuto_w		:= 0;
else
	begin

	dt_atual_w		:= dt_inicial_p;
	dt_anterior_w		:= dt_inicial_p;
	qt_min_ref_w		:= coalesce(qt_min_ref_p,1);
	qt_minuto_w		:= 0;

	while(trunc(dt_atual_w, 'dd') < trunc(dt_final_p, 'dd')) loop
		begin

		dt_atual_w	:= dt_atual_w + 1;

		select	to_char(pkg_date_utils.is_business_day(dt_atual_w,0)),
			obter_se_feriado(cd_estabelecimento_p, trunc(dt_atual_w, 'dd'))
		into STRICT	ie_dia_semana_w,
			ie_feriado_w
		;

		if (ie_dia_semana_w = '0') and (ie_feriado_w = 0) then
			begin

			qt_minuto_w	:= qt_minuto_w + round((dt_atual_w - dt_anterior_w) * 1440);

			end;
		end if;
		dt_anterior_w	:= dt_atual_w;

		end;
	end loop;

	qt_minuto_w		:= round(qt_minuto_w / qt_min_ref_w) * qt_min_ref_w;

	end;
end if;

return qt_minuto_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
