CREATE OR REPLACE FUNCTION obter_min_entre_datas_null ( dt_inicial_p timestamp, dt_final_p timestamp, qt_min_ref_p bigint) RETURNS bigint AS $body$
DECLARE


qt_minuto_w			bigint;
qt_min_ref_w		smallint;


BEGIN

if (coalesce(dt_inicial_p::text, '') = '') or (coalesce(dt_final_p::text, '') = '') then
	qt_minuto_w		:= null;
else
	begin
	qt_min_ref_w	:= coalesce(qt_min_ref_p,1);
	qt_minuto_w		:= round((dt_final_p - dt_inicial_p) * 1440);
	qt_minuto_w		:= round(qt_minuto_w / qt_min_ref_w) * qt_min_ref_w;
	end;
end if;

return qt_minuto_w;

END;
$body$
