CREATE OR REPLACE FUNCTION obter_min_sala_ociosa_painel (nr_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w	bigint	:= null;
dt_entrada_unidade_w	timestamp		:= null;
dt_termino_w		timestamp		:= null;
qt_tempo_w		double precision 	:= 0;
ie_zera_tempo_w		varchar(1);
dt_inicio_real_w	timestamp		:= null;
cd_setor_atendimento_w	integer;
cd_unidade_basica_w	varchar(10);
cd_unidade_compl_w	varchar(10);



BEGIN
begin
if (coalesce(nr_cirurgia_p,0) > 0) then

	select	max(b.cd_setor_atendimento),
		max(b.cd_unidade_basica),
		max(b.cd_unidade_compl),
		max(a.dt_termino)
	into STRICT	cd_setor_atendimento_w,
		cd_unidade_basica_w,
		cd_unidade_compl_w,
		dt_termino_w
	from	cirurgia a,
		atend_paciente_unidade b
	where	a.dt_entrada_unidade 	= b.dt_entrada_unidade
	and	a.nr_atendimento	= b.nr_atendimento
	and	a.nr_cirurgia 		= nr_cirurgia_p;

	if (dt_termino_w IS NOT NULL AND dt_termino_w::text <> '') then
		select	min(dt_inicio_real)
		into STRICT	dt_inicio_real_w
		from	cirurgia a,
			atend_paciente_unidade b
		where	a.dt_entrada_unidade 	= b.dt_entrada_unidade
		and	a.nr_atendimento	= b.nr_atendimento
		and	b.cd_setor_atendimento	= cd_setor_atendimento_w
		and	b.cd_unidade_basica	= cd_unidade_basica_w
		and	b.cd_unidade_compl	= cd_unidade_compl_w
		and	a.ie_status_cirurgia	<> 4
		and	a.dt_inicio_real	> dt_termino_w;

		if (coalesce(dt_inicio_real_w::text, '') = '') then
			qt_tempo_w := trunc((clock_timestamp() - dt_termino_w) * 1440);
		else
			qt_tempo_w := trunc((dt_inicio_real_w - dt_termino_w) * 1440);
		end if;
	end if;
end if;
exception
	when others then
	qt_tempo_w := 0;
end;

return 	qt_tempo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
