CREATE OR REPLACE FUNCTION obter_mobilidade_barthel (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN

select 	CASE WHEN IE_MOBILIDADE=0 THEN 'Imóvel ou < 45 m' WHEN IE_MOBILIDADE=5 THEN 'Cadeira de rodas indenpendentes incluindo cantos > 45 m' WHEN IE_MOBILIDADE=10 THEN 'Anda com ajuda de uma pessoa (verbal ou física) > 45 m' WHEN IE_MOBILIDADE=15 THEN 'Independente mas pode usar um auxilio ex: bengala > 45 m' END
into STRICT	ds_retorno_w
from	escala_barthel
where	nr_sequencia = nr_sequencia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
