CREATE OR REPLACE FUNCTION obter_modelo_form_receita (cd_modelo_formatacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  varchar(80) := '';


BEGIN
if (coalesce(cd_modelo_formatacao_p, 0) <> 0) then
	select  coalesce(ds_titulo, '')
	into STRICT	ds_retorno_w
	from    medic_mascara_receita
	where   ((cd_estabelecimento = obter_estabelecimento_ativo()) or (coalesce(cd_estabelecimento::text, '') = ''))
	and     nr_sequencia = cd_modelo_formatacao_p;
end if;

RETURN  ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
