CREATE OR REPLACE FUNCTION obter_modelo_intervencao ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_retorno_w					bigint := 0;
nr_seq_modelo_w					bigint;
nr_seq_proc_w					bigint;
ie_possui_modelo_w				varchar(1) := 'N';
ie_possui_modelo_temp_w			varchar(1);

c01 CURSOR FOR
	SELECT	distinct b.nr_seq_modelo
	from 	pe_prescr_proc a,
			pe_interv_modelo b
	where 	a.nr_seq_prescr = nr_sequencia_p
	and	  	a.nr_seq_proc = b.nr_seq_proc;

c02 CURSOR FOR
	SELECT	nr_seq_proc
	from	pe_prescr_proc
	where	nr_seq_prescr = nr_sequencia_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_modelo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	open c02;
	loop
	fetch c02 into
		nr_seq_proc_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		select	coalesce(max('S'),'N')
		into STRICT	ie_possui_modelo_temp_w
		from 	pe_prescr_proc a,
				pe_interv_modelo b
		where 	nr_seq_prescr = nr_sequencia_p
		and	  	a.nr_seq_proc = b.nr_seq_proc
		and		nr_seq_modelo_w = b.nr_seq_modelo
		and		a.nr_seq_proc = nr_seq_proc_w;

		if (ie_possui_modelo_temp_w = 'S') then
			ie_possui_modelo_w := 'S';
		else
			ie_possui_modelo_w := 'N';
			exit;
		end if;

		end;
	end loop;
	close c02;

	if (ie_possui_modelo_w = 'S') then
		nr_retorno_w := nr_seq_modelo_w;
		exit;
	end if;

	end;
end loop;
close c01;

return nr_retorno_w;

end;
$body$
