CREATE OR REPLACE FUNCTION obter_modo_medicamento_cirur ( nr_seq_medicamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_modo_adm_w				varchar(5);
ie_tipo_dosagem_w			varchar(15);
cd_unid_medida_adm_w		varchar(30);


BEGIN

select	max(ie_modo_adm),
		max(ie_tipo_dosagem),
		max(cd_unid_medida_adm)
into STRICT	ie_modo_adm_w,
		ie_tipo_dosagem_w,
		cd_unid_medida_adm_w
from	cirurgia_agente_anestesico
where	nr_sequencia	=	nr_seq_medicamento_p
and	coalesce(ie_situacao,'A') = 'A';

if (ie_modo_adm_w = 'AC') then
	if (ie_tipo_dosagem_w IS NOT NULL AND ie_tipo_dosagem_w::text <> '') then
		ie_modo_adm_w	:= 'CO';
	else
		ie_modo_adm_w	:= 'IN';
	end if;
end if;

return	ie_modo_adm_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
