SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_moeda_item_conv ( cd_edicao_amb_p bigint, cd_procedimento_p bigint, dt_consulta_p timestamp, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
C = Codigo 
D = Descricao 
*/
	 
 
cd_moeda_w	bigint;
ds_moeda_w	varchar(30);
ds_retorno_w	varchar(30);


BEGIN 
 
begin 
select	a.cd_moeda, 
	substr(obter_desc_moeda(a.cd_moeda),1,30) 
into STRICT	cd_moeda_w, 
	ds_moeda_w 
from	preco_amb a 
where (a.cd_edicao_amb  = cd_edicao_amb_p) 
and (a.cd_procedimento = cd_procedimento_p) 
and	coalesce(a.dt_inicio_vigencia,clock_timestamp() - interval '3650 days')	= 
	 (	SELECT	max(coalesce(b.dt_inicio_vigencia,clock_timestamp() - interval '3650 days')) 
		from 	preco_amb b 
		where 	b.cd_edicao_amb	= cd_edicao_amb_p 
		and	b.cd_procedimento	= cd_procedimento_p 
		and	coalesce(b.dt_inicio_vigencia,clock_timestamp() - interval '3650 days')	<= dt_consulta_p);
exception 
when others then 
	cd_moeda_w := 1;
	ds_moeda_w := '';
end;
 
if (ie_tipo_p = 'C') then 
	ds_retorno_w := to_char(cd_moeda_w);
else 
	ds_retorno_w := ds_moeda_w;
end if;
	 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
