CREATE OR REPLACE FUNCTION obter_moeda_padrao_empresa (cd_pesquisa_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*-- IE_OPCAO_P
 -- E - Empresa
 -- B - Banco
*/
cd_moeda_w	integer;


BEGIN
	if ie_opcao_p = 'E' then
		begin
		select a.cd_moeda_ref
		into STRICT cd_moeda_w
		from empresa a,
			estabelecimento b
		where a.cd_empresa = b.cd_empresa
		  and b.cd_estabelecimento = cd_pesquisa_p
		  and (a.cd_moeda_ref IS NOT NULL AND a.cd_moeda_ref::text <> '');
		exception when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(341435);
		end;
	elsif ie_opcao_p = 'B' then
		select max(cd_moeda)
		into STRICT cd_moeda_w
		from banco_estabelecimento
		where nr_sequencia = cd_pesquisa_p;
	end if;

	return cd_moeda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
