CREATE OR REPLACE FUNCTION obter_moeda_padrao (cd_estabelecimento_p bigint, ie_conta_receb_pagar_p text) RETURNS bigint AS $body$
DECLARE


cd_moeda_w	integer;


BEGIN

if (ie_conta_receb_pagar_p	= 'R') then
	begin

	select	cd_moeda_padrao
	into STRICT	cd_moeda_w
	from	parametro_contas_receber
	where	cd_estabelecimento	= cd_estabelecimento_p;


	end;
elsif (ie_conta_receb_pagar_p	= 'P') then
	begin

	select	cd_moeda_padrao
	into STRICT	cd_moeda_w
	from	parametros_contas_pagar
	where	cd_estabelecimento	= cd_estabelecimento_p;

	end;
end if;

return	cd_moeda_w;

end;
$body$
