SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_monit_respiratoria ( nr_atendimento_p bigint, ie_sinal_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		double precision;


BEGIN

if (nr_atendimento_p > 0) and (ie_sinal_p IS NOT NULL AND ie_sinal_p::text <> '') then
	begin
		
	if (ie_sinal_p = 'FIO2') then
		select	max(qt_fio2)
		into STRICT	vl_retorno_w
		from	atendimento_monit_resp	
		where	nr_sequencia = (SELECT	coalesce(max(nr_sequencia),-1)
					from	atendimento_monit_resp
					where	(qt_fio2 IS NOT NULL AND qt_fio2::text <> '')
					and		nr_atendimento	= nr_atendimento_p
					and		ie_situacao = 'A');
	elsif (ie_sinal_p = 'PEEP') then
		select	max(qt_peep)
		into STRICT	vl_retorno_w
		from	atendimento_monit_resp	
		where	nr_sequencia = (SELECT	coalesce(max(nr_sequencia),-1)
					from	atendimento_monit_resp
					where	(qt_peep IS NOT NULL AND qt_peep::text <> '')
					and		nr_atendimento	= nr_atendimento_p
					and		ie_situacao = 'A');
	elsif (ie_sinal_p = 'TI/TE') then
		select	max(qt_ti / qt_te)
		into STRICT	vl_retorno_w
		from	atendimento_monit_resp	
		where	nr_sequencia = (SELECT	coalesce(max(nr_sequencia),-1)
					from	atendimento_monit_resp
					where	(qt_ti IS NOT NULL AND qt_ti::text <> '')
					and		(qt_te IS NOT NULL AND qt_te::text <> '')
					and		nr_atendimento	= nr_atendimento_p
					and		ie_situacao = 'A');
	elsif (ie_sinal_p = 'TITE') then
		select	max(qt_ti_te)
		into STRICT	vl_retorno_w
		from	atendimento_monit_resp	
		where	nr_sequencia = (SELECT	coalesce(max(nr_sequencia),-1)
					from	atendimento_monit_resp
					where	(qt_ti_te IS NOT NULL AND qt_ti_te::text <> '')
					and		nr_atendimento	= nr_atendimento_p
					and		ie_situacao = 'A');
	elsif (ie_sinal_p = 'CUFFMMHG') then
		select	max(qt_pressao_cuff)
		into STRICT	vl_retorno_w
		from	atendimento_monit_resp	
		where	nr_sequencia = (SELECT	coalesce(max(nr_sequencia),-1)
					from	atendimento_monit_resp
					where	(qt_pressao_cuff IS NOT NULL AND qt_pressao_cuff::text <> '')
					and		nr_atendimento	= nr_atendimento_p
					and		ie_situacao = 'A');		
		end if;
	end;
end if;

return vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_monit_respiratoria ( nr_atendimento_p bigint, ie_sinal_p text) FROM PUBLIC;

