CREATE OR REPLACE FUNCTION obter_motivo_atraso_agecons ( nr_sequencia_p bigint, cd_motivo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_motivo_atraso_w	varchar(255);
ds_motivo_atraso_pac_w	varchar(255);
ds_retorno_w		varchar(255);
cd_estabelecimento_w	smallint;
					

BEGIN 
select	obter_estab_agenda(cd_agenda) 
into STRICT	cd_estabelecimento_w 
from	agenda_consulta 
where	nr_sequencia = nr_sequencia_p;
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	select	max(substr(ds_motivo_atraso,1,255)), 
		max(substr(obter_motivo_atraso_pac(cd_motivo_p, cd_estabelecimento_w),1,255)) 
	into STRICT	ds_motivo_atraso_w, 
		ds_motivo_atraso_pac_w 
	from	agenda_consulta 
	where	nr_sequencia	= nr_sequencia_p;	
end if;
 
	ds_retorno_w	:= substr(ds_motivo_atraso_w||' '||ds_motivo_atraso_pac_w,1,255);
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
