CREATE OR REPLACE FUNCTION obter_motivo_auditoria (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_w		varchar(80);


BEGIN

begin
select	coalesce(max(ds_motivo_auditoria),Wheb_mensagem_pck.get_texto(799244))
into STRICT	ds_motivo_w
from 	auditoria_motivo
where 	nr_sequencia = nr_sequencia_p;
exception
