SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_motivo_cancel_agecons (nr_seq_agenda_p bigint, cd_motivo_cancel_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_obs_w	varchar(255);
ds_motivo_dom_w	agenda_motivo_cancelamento.ds_motivo%type;
ds_motivo_w		varchar(255);
ds_responsavel_w	varchar(40);
nm_usuario_status_w	varchar(15);


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	/* obter motivo observacao */

	select	max(ds_motivo_status),
		'(' || max(nm_usuario_status) || ' em ' || to_char(max(dt_status),'dd/mm/yyyy hh24:mi:ss') || ')',
		max(nm_usuario_status)
	into STRICT	ds_motivo_obs_w,
		ds_responsavel_w,
		nm_usuario_status_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	/* obter motivo cadastro */

	/*
	select	max(ds_valor_dominio)
	into	ds_motivo_dom_w
	from	valor_dominio
	where	cd_dominio = 1011
	and	vl_dominio = cd_motivo_cancel_p;
	*/
	/*
	select	nvl(obter_desc_motivo_agenda(somente_numero(cd_motivo_cancel_p)),obter_valor_dominio(1011,cd_motivo_cancel_p))
	into	ds_motivo_dom_w
	from	dual;
	*/
	select	max(ds_motivo)
	into STRICT	ds_motivo_dom_w
	from	agenda_motivo_cancelamento
	where	cd_motivo = cd_motivo_cancel_p
	and	coalesce(ie_situacao,'A') = 'A'
	and	coalesce(ie_agenda,'T') in ('C','T')
	and	((cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento) or (coalesce(cd_estabelecimento::text, '') = ''));

	if (nm_usuario_status_w IS NOT NULL AND nm_usuario_status_w::text <> '') and (ds_motivo_obs_w IS NOT NULL AND ds_motivo_obs_w::text <> '') and (ds_motivo_dom_w IS NOT NULL AND ds_motivo_dom_w::text <> '') then
		ds_motivo_w := substr(ds_motivo_obs_w || ' - ' || ds_motivo_dom_w || ' ' || ds_responsavel_w,1,255);
	elsif (ds_motivo_dom_w IS NOT NULL AND ds_motivo_dom_w::text <> '') then
		ds_motivo_w := substr(ds_motivo_dom_w || ' - ' || ds_responsavel_w,1,255);
	else
		ds_motivo_w := substr(coalesce(ds_motivo_obs_w, ds_motivo_dom_w || ' ' || ds_responsavel_w),1,255);
	end if;
end if;

return ds_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
